import { Component, OnInit, Input } from '@angular/core';
import {Router} from '@angular/router';

@Component({
  selector: 'app-work',
  templateUrl: './work.component.html',
  styleUrls: ['./work.component.css']
})
export class WorkComponent {

  @Input() clickedHomeNavItem : any; //This is how the parent notifies the child of the data it is passing

  constructor(  private router: Router ) {
    // console.log("This is the about me console log showing which route is currently being visited:", this.router.url);
  }

  ngOnInit() {
  }
}
<div class="work-wrapper">
    <h1 class="title">Work</h1>
    <div class="business-card">
        <div class="row">
            <div class="text-info">
                <p class="name">Chioma Ubogagu</p>
                <p class="position">CMS Developer at Simpleview</p>
            </div>
        </div>
        <div class="row">
            <img class="qr-code" src="/assets/images/qr-code-new.png" alt="QR Code" >
        </div>
    </div>
</div>
